  "python": {
    "library": "import json


def ruff_format(cell_text):
    import os, re, subprocess
    from ruff.__main__ import find_ruff_bin

    ruff = find_ruff_bin()

    cell_text = re.sub("^%", "#%#", cell_text, flags=re.M)

    env = dict(os.environ)
    env["NO_COLOR"] = "1"
    for c in ["FORCE_COLOR", "CLICOLOR", "CLICOLOR_FORCE"]:
        env.pop(c, None)
    proc = subprocess.run(
        [ruff, "format", "-"],
        text=True,
        input=cell_text,
        stdout=subprocess.PIPE,
        stderr=subprocess.PIPE,
        env=env,
    )
    if proc.returncode != 0:
        raise ValueError(f"Failed to format: {proc.stderr}")

    formatted = proc.stdout

    return re.sub("^#%#", "%", formatted, flags=re.M)",
    "prefix": "print(json.dumps(ruff_format(u",
    "postfix": ")))"
  }
}
